import React, { Component } from 'react';
import './App.css';
import Table from 'react-bootstrap/Table';

class App extends Component{
  componentDidMount() {
    fetch("https://ghibliapi.herokuapp.com/films")
      .then(res => res.json())
      .then(
        (result) => {
         console.log('Result', result);
        },
        (error) => {
          console.log('Error', error);
        }
      )
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
            Movies Table
        </header>
        <Table striped bordered hover>
    <thead>
      <tr>
        <th>#</th>
        <th>First Name</th>
        <th>Last Name</th>
        <th>Username</th>
      </tr>
    </thead>
    <tbody>
      <tr>
        <td>1</td>
        <td>Mark</td>
        <td>Otto</td>
        <td>@mdo</td>
      </tr>
      <tr>
        <td>2</td>
        <td>Jacob</td>
        <td>Thornton</td>
        <td>@fat</td>
      </tr>
      <tr>
        <td>3</td>
        <td colSpan="2">Larry the Bird</td>
        <td>@twitter</td>
      </tr>
    </tbody>
  </Table>
      </div>
    );
  }
}

export default App;
