import React, { Component } from 'react';
import './App.css';
import { stat } from 'fs';

class App extends Component{
  constructor() {
    super();
    this.state = {
      n: '',
    }
  }


  componentDidUpdate(props, state) {
    console.log('props', props, 'state', state);
    document.getElementById('chessBoard').innerHTML = "";
    var chessBoard = document.getElementById('chessBoard');
    for (var i = 0; i < this.state.n; ++i){
      var row = document.createElement('div')
      row.className = 'row'
      row.style.flexDirection = i % 2 === 0 ? '' : 'row-reverse'; 
      for (var j = 0; j < this.state.n; ++j){
        var column = document.createElement('div')
        column.className = 'column'
        column.style.backgroundColor = j % 2 === 0 ? 'white' : 'black'
        row.appendChild(column)
      }
      chessBoard.appendChild(row)
    }   
   }

   handleValueOfN(ele) {
     console.log('ele', ele.target.value);
     this.setState({
       n: ele.target.value,
     })
   }

  render() {
    return (
      <div className="app">
        <input value={this.state.n} onChange={(ele) => this.handleValueOfN(ele)}></input>
        <div id="chessBoard" className="chessBoard">
        </div>
      </div>
    );
  }
}
export default App;
