import React from "react";
import './Modal.css';

const Modal = ({ handleClose, show, currentMovie }) => {
    const showHideClassName = show ? 'modal display-block' : 'modal display-none';
    console.log('Current Movei', currentMovie);
    return (
      <div className={showHideClassName}>
        <section className='modal-main'>
          <div> Movie Name: currentMovie ? currentMovie.title : ''} </div>
          <div>The Movie Title</div>
          <div>The Director’s Name</div>
          <div>Movie Description/Summary</div>
          <div>Producer’s Name</div>
          <div>Year of Release</div>
          <div>Movie’s Rating(IMDB)</div>
          <button
            onClick={handleClose}
          >
            Close
          </button>
        </section>
      </div>
    );
  };

  export default Modal;
