import React from "react";
import './Modal.css';

const Modal = ({ handleClose, show, currentMovie }) => {
    const showHideClassName = show ? 'modal display-block' : 'modal display-none';
    console.log('Current Movei', currentMovie);
    return (
      <div className={showHideClassName}>
        <section className='modal-main'>
          <div> <span>Movie Name: </span>{currentMovie ? currentMovie.title : '' }</div>
          <div> <span>The Movie Title: </span>{currentMovie ? currentMovie.title : '' }</div>
          <div> <span>The Director’s Name: : </span>{currentMovie ? currentMovie.director : ''}</div>
          <div> <span>Movie Description/Summary: </span>{currentMovie ? currentMovie.description : ''}</div>
          <div> <span>Producer’s Name : </span>{currentMovie ? currentMovie.producer : ''}</div>
          <div> <span>Year of Release: </span>:  {currentMovie ? currentMovie.release_date : ''}</div>
          <div> <span>Movie’s Rating(IMDB): </span>{ currentMovie ? currentMovie.rt_score : ''}</div>
          <button
            onClick={handleClose}
          >
            Close
          </button>
        </section>
      </div>
    );
  };

  export default Modal;
