import React, { Component } from 'react';
import './App.css';
import Table from 'react-bootstrap/Table';
import Modal from './Modal';

class App extends Component{
  constructor() {
    super();
    this.state = {
      movieItems: [],
      show: false,
      currentMovie: null,
    }
  }

  componentDidMount() {
    fetch("https://ghibliapi.herokuapp.com/films")
      .then(res => res.json())
      .then(
        (result) => {
         this.setState({
           movieItems: result,
         })
        },
        (error) => {
          console.log('Error', error);
        }
      )
  }

  showModal = (item) => {
    this.setState({ 
      show: true,
      currentMovie: item,
    });
  }

  hideModal = () => {
    this.setState({ show: false });
  }

  render() {
    console.log('Movie Items', this.state.movieItems);
    const items = this.state.movieItems;
    return (
      <div className="App">
        <header className="App-header">
            Movies Table
        </header>
        <hr />
        <Table striped bordered hover>  
          <thead>
            <tr>
              <th>The Movie Title</th>
              <th>The Director’s Name</th>
              <th>Movie’s Rating</th>
              <th>Movie Poster(</th>
            </tr>
          </thead>
          <tbody>
            {items.map(item => (
                <tr key={item.id}>
                  <td onClick={() => this.showModal(item)}>{item.title}</td>
                  <td>{item.director}</td>
                  <td>{item.rt_score}</td>
                  <td>{item.url}</td>
                </tr>
              ))}
          </tbody>    
        </Table>
        <Modal show={this.state.show} handleClose={this.hideModal} currentMovie={this.state.currentMovie}>
        </Modal>
      </div>
    );
  }
}

export default App;
