import React, { Component } from 'react';
import './App.css';
import Table from 'react-bootstrap/Table';

class App extends Component{
  constructor() {
    super();
    this.state = {
      movieItems: [],
    }
  }
  componentDidMount() {
    fetch("https://ghibliapi.herokuapp.com/films")
      .then(res => res.json())
      .then(
        (result) => {
         this.setState({
           movieItems: result,
         })
        },
        (error) => {
          console.log('Error', error);
        }
      )
  }

  handleClick() {
    console.log('itenm');
  }

  render() {
    console.log('Movie Items', this.state.movieItems);
    const items = this.state.movieItems;
    return (
      <div className="App">
        <header className="App-header">
            Movies Table
        </header>
        <Table>
        <th>
          <td>The Movie Title</td>
          <td>The Director’s Name</td>
          <td>Movie’s Rating</td>
          <td>Movie Poster(</td>
        </th>

        {items.map(item => (
            <tr key={item.id} onClick={(item) => this.handleClick(item)}>
              <td>{item.title}</td>
              <td>{item.director}</td>
              <td>{item.rt_score}</td>
              <td>{item.url}</td>
            </tr>
          ))}
        <tr>

        </tr>
        </Table>
        {/* {items.map(item => (
            <li key={item.id} onClick={(item) => this.handleClick(item)}>
              {item.title}
            </li>
          ))} */}
      </div>
    );
  }
}

export default App;
