import React, { Component } from 'react';
import './App.css';
import Table from 'react-bootstrap/Table';

class App extends Component{
  constructor() {
    super();
    this.state = {
      movieItems: [],
    }
  }
  componentDidMount() {
    fetch("https://ghibliapi.herokuapp.com/films")
      .then(res => res.json())
      .then(
        (result) => {
         this.setState({
           movieItems: result,
         })
        },
        (error) => {
          console.log('Error', error);
        }
      )
  }

  render() {
    console.log('Movie Items', this.state.movieItems);
    const items = this.state.movieItems;
    return (
      <div className="App">
        <header className="App-header">
            Movies Table
        </header>
        <ul>
        {items.map(item => (
            <li key={item.id}>
              <span onClick={alert(item)}>{item.title}</span>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default App;
